function setColors(nodes, limits, is_greater) {
    for(var i = 0; i < nodes.length; i++){
        var td = nodes[i];
        var td_value =  parseFloat(td.textContent.replace('%', ''));
        if(isNaN(td_value)){
            var class_name = 'td-color-na';
        } else {
            class_name = evalLimits(td_value, limits, is_greater);
        }
        td.setAttribute('class', class_name);
    }
}
function evalLimits(value, limits, is_greater){
    if(limits.length === 1){
        if (is_greater === ''){
            if (value < limits[0]) {
                return 'td-color-7';
            } else if (value === limits[0]) {
                return 'td-color-4';
            }
            return 'td-color-1';
        } else if (is_greater) {
            if (value > limits[0]) {
                return 'td-color-7';
            }
            return 'td-color-4';
        } else {
            if (value < limits[0]) {
                return 'td-color-1';
            } else if (value === limits[0]) {
                return 'td-color-4';
            }
            return 'td-color-7';

        }
    } else{
        for(var i = 0; i < limits.length; i++) {
            if (is_greater){
                if (value > limits[i]) {
                    return 'td-color-' + String(i);
                }
            } else {
                if (value < limits[i]) {
                    return 'td-color-' + String(i);
                }
            }
        }
        return 'td-color-7';
    }
}
